$(document).ready(function(){

	$('.owl_carouselle').owlCarousel({
      	autoplay:true,
    	loop:true,
		nav: true,
		navText: ["<i class='fa fa-chevron-left'></i>","<i class='fa fa-chevron-right'></i>"],
		smartSpeed: 900,
    	responsiveClass:true,
    	responsive:{
        	0:{
            	items:1,
            	nav:true
        	},
        	600:{
            	items:1,
            	nav:false
        	},
        	1000:{
            	items:1,
            	nav:true,
        	}
    	}
	});

	$('.blog_menus').slicknav({
		label: '',
		duration: 200,
  		prependTo: '#slick-nav'
	});

	$('.top_menu_bar ul').slicknav({
		label: '',
		duration: 200,
  		prependTo: '#top_nav'
	});

});
